INSERT INTO places (id, name, category, subcategory, description, history, nearby_recommendations, personal_tips, lat, lng, opening_hours, price, best_time, past_events, sentiment_tags, image, source_url) VALUES
('1', 'Victoria Memorial', 'Landmark', 'Museum', 'A grand marble building dedicated to Queen Victoria, surrounded by sprawling gardens.', 'Built between 1906–1921 to commemorate Queen Victoria''s 25-year reign in India. Designed by William Emerson in Indo-Saracenic revivalist style.', NULL, 'The best time for photos is just before sunset when the white marble glows gold. Don''t miss the light and sound show in the evening.', 22.5448, 88.3426, NULL, 'Museum: ₹30 (Indians), ₹500 (Foreigners); Garden: ₹20', 'October to March, late afternoon', 'Hosted the G20 cultural dinner in 2023.', NULL, 'https://upload.wikimedia.org/wikipedia/commons/7/72/Victoria_Memorial_Kolkata_India.jpg', 'https://en.wikipedia.org/wiki/Victoria_Memorial,_Kolkata'),
('2', 'Howrah Bridge', 'Landmark', 'Bridge', 'An iconic cantilever bridge spanning the Hooghly River, a symbol of Kolkata.', 'Commissioned in 1943, it was the third-longest cantilever bridge at the time. It has no nuts and bolts; it''s held together by rivets.', NULL, 'Walk across it early morning to see the city wake up. Avoid peak hours if you dislike crowds.', 22.5851, 88.3468, NULL, 'Free', 'Dawn or Dusk', 'Featured in films like Lion and Yuva.', NULL, 'https://upload.wikimedia.org/wikipedia/commons/d/d0/Howrah_Bridge_2020.jpg', 'https://en.wikipedia.org/wiki/Howrah_Bridge'),
('3', 'Indian Museum', 'Museum', 'History', 'The oldest and largest museum in India, housing rare antiques, armor, and fossils.', 'Founded in 1814 by the Asiatic Society of Bengal. It has a rare collection of Egyptian mummies.', NULL, 'It''s huge! Focus on the Archaeology and Art sections if you are short on time. The Egyptian mummy is a must-see.', 22.5579, 88.3511, NULL, '₹75 (Indians), ₹500 (Foreigners)', 'Afternoon (good for escaping heat)', 'Bicentenary celebrations in 2014.', NULL, 'https://upload.wikimedia.org/wikipedia/commons/6/6a/Indian_Museum_Kolkata.jpg', 'https://en.wikipedia.org/wiki/Indian_Museum,_Kolkata'),
('4', 'College Street Coffee House', 'Food & Drink', 'Cafe', 'A historic cafe that has been the hub of Kolkata''s intelligentsia for decades.', 'Originally Albert Hall, founded in 1876. It became the meeting place for poets, artists, and revolutionaries like Satyajit Ray and Amartya Sen.', NULL, 'Don''t expect quick service or luxury. Go for the adda (chat) and nostalgia. Try the Cold Coffee and Mutton Afgani.', 22.5759, 88.3643, NULL, '₹200 for two', 'Evening for the buzz', 'Countless literary debates and book launches.', NULL, 'https://upload.wikimedia.org/wikipedia/commons/thumb/2/23/Coffee_House_Kolkata.jpg/1200px-Coffee_House_Kolkata.jpg', 'https://en.wikipedia.org/wiki/Indian_Coffee_House'),
('5', 'Kumartuli', 'Cultural', 'Artisan District', 'A traditional potters'' quarter where clay idols of gods and goddesses are made.', 'The settlement dates back to the British era. It is famous for sculpting idols for Durga Puja.', NULL, 'Visit a month before Durga Puja (September) to see the idols being painted. Ask permission before taking close-up photos of artisans.', 22.5968, 88.3654, NULL, 'Free', 'August-September', 'Durga Puja preparation frenzy.', NULL, 'https://upload.wikimedia.org/wikipedia/commons/3/3e/Kumartuli_Idol_Making.jpg', 'https://en.wikipedia.org/wiki/Kumartuli'),
('6', 'Prinsep Ghat', 'Landmark', 'Riverside', 'A beautiful Palladian porch on the banks of the Hooghly River.', 'Built in 1841 in memory of James Prinsep, a scholar who deciphered the Brahmi script.', NULL, 'Take a private boat ride on the river at sunset. It''s very romantic and offers great views of the Vidyasagar Setu.', 22.5505, 88.3348, NULL, 'Free (Boat rides chargeable)', 'Sunset', 'Cultural festivals and shoots.', NULL, 'https://upload.wikimedia.org/wikipedia/commons/9/98/Prinsep_Ghat_Kolkata.jpg', 'https://en.wikipedia.org/wiki/Prinsep_Ghat'),
('7', 'Dakshineswar Kali Temple', 'Temple', 'Pilgrimage', 'A famous Hindu navaratna temple located on the eastern bank of the Hooghly River.', 'Built in 1855 by Rani Rashmoni. The mystic Ramakrishna Paramahansa served as its priest.', NULL, 'The queue can be very long. Tuesdays and Saturdays are busiest. Use the Skywalk for easy access from the station.', 22.6544, 88.3571, NULL, 'Free', 'Early morning', 'Kali Puja', NULL, 'https://upload.wikimedia.org/wikipedia/commons/6/68/Dakshineswar_Kali_Temple_Kolkata.jpg', 'https://en.wikipedia.org/wiki/Dakshineswar_Kali_Temple'),
('8', 'Park Street', 'Area', 'Food & Nightlife', 'The street that never sleeps, famous for its restaurants, pubs, and colonial charm.', 'Was the center of Kolkata''s nightlife in the swinging 60s. Home to iconic eateries like Flurys and Moulin Rouge.', NULL, 'Visit during Christmas! The lighting is spectacular. Try the Chelo Kebab at Peter Cat.', 22.5492, 88.3494, NULL, 'Variable', 'December evenings', 'Christmas Carnival', NULL, 'https://upload.wikimedia.org/wikipedia/commons/c/c6/Park_Street_Kolkata_Christmas.jpg', 'https://en.wikipedia.org/wiki/Park_Street,_Kolkata'),
('9', 'Marble Palace', 'Heritage', 'Mansion', 'A palatial nineteenth-century mansion in North Kolkata, famous for its marble walls and floors.', 'Built in 1835 by Raja Rajendra Mullick. It houses a private zoo and a collection of Western art.', NULL, 'You need a permit from the West Bengal Tourism Information Bureau 24 hours in advance. Photography is strictly prohibited.', 22.5825, 88.3597, NULL, 'Free (Permit required)', 'Morning', 'Private family events.', NULL, 'https://upload.wikimedia.org/wikipedia/commons/8/86/Marble_Palace_Kolkata.jpg', 'https://en.wikipedia.org/wiki/Marble_Palace_(Kolkata)'),
('10', 'South Park Street Cemetery', 'Heritage', 'Cemetery', 'An 18th-century non-church cemetery with massive gothic tombs.', 'Opened in 1767, it is one of the earliest non-church cemeteries in the world. It houses the grave of Henry Louis Vivian Derozio.', NULL, 'It''s surprisingly peaceful. Great for history buffs and photographers. Can be a bit spooky!', 22.5440, 88.3508, NULL, '₹20', 'Winter afternoon', 'Heritage walks', NULL, 'https://upload.wikimedia.org/wikipedia/commons/5/5e/South_Park_Street_Cemetery.jpg', 'https://en.wikipedia.org/wiki/South_Park_Street_Cemetery'),
('11', 'Eco Park', 'Park', 'Recreation', 'The biggest urban park in India, featuring themed areas like the Seven Wonders of the World.', 'Inaugurated in 2012 in New Town to promote eco-tourism.', NULL, 'It''s massive. Rent a cycle or take the toy train. The Japanese Garden is very serene.', 22.6037, 88.4656, NULL, '₹30', 'Winter evenings', 'Concerts and Food Festivals', NULL, 'https://upload.wikimedia.org/wikipedia/commons/7/7a/Eco_Park_Kolkata.jpg', 'https://en.wikipedia.org/wiki/Eco_Park,_New_Town'),
('12', 'Kalighat Kali Temple', 'Temple', 'Religious', 'One of the 51 Shakti Peethas, dedicated to the goddess Kali.', 'The current temple was built in 1809, but the site has been sacred for centuries. The name Calcutta is said to be derived from Kalighat.', NULL, 'Beware of aggressive pandas (priests). Keep your belongings safe. The morning aarti is powerful.', 22.5186, 88.3420, NULL, 'Free', 'Early morning', 'Daily worship', NULL, 'https://upload.wikimedia.org/wikipedia/commons/f/f6/Kalighat_Kali_Temple.jpg', 'https://en.wikipedia.org/wiki/Kalighat_Kali_Temple'),
('13', 'Belur Math', 'Temple', 'Spiritual', 'The headquarters of the Ramakrishna Math and Mission.', 'Founded by Swami Vivekananda in 1897. The architecture fuses Hindu, Christian, and Islamic motifs to symbolize unity of religions.', NULL, 'Sit by the river bank in the evening. It is incredibly peaceful. Silence is strictly maintained.', 22.6326, 88.3560, NULL, 'Free', 'Evening Aarti', 'Durga Puja (Kumari Puja is famous)', NULL, 'https://upload.wikimedia.org/wikipedia/commons/1/16/Belur_Math_Kolkata.jpg', 'https://en.wikipedia.org/wiki/Belur_Math'),
('14', 'New Market', 'Market', 'Shopping', 'A historic shopping destination that sells everything from clothes to cheese.', 'Opened in 1874 as Sir Stuart Hogg Market. It was the first municipal market in Kolkata.', NULL, 'Bargain hard! Visit Nahoum''s Bakery for their rich fruit cake. It''s a maze, so try not to get lost.', 22.5586, 88.3524, NULL, 'Variable', 'Weekdays', 'Christmas shopping rush', NULL, 'https://upload.wikimedia.org/wikipedia/commons/8/8f/New_Market_Kolkata.jpg', 'https://en.wikipedia.org/wiki/New_Market,_Kolkata'),
('15', 'Jorasanko Thakur Bari', 'Heritage', 'Museum', 'The ancestral home of the Tagore family.', 'Birthplace of Nobel laureate Rabindranath Tagore. It is now a museum dedicated to his life and works.', NULL, 'A must for literature lovers. The light and sound show is decent.', 22.5850, 88.3594, NULL, '₹20 (Indians), ₹150 (Foreigners)', 'Afternoon', 'Rabindra Jayanti', NULL, 'https://upload.wikimedia.org/wikipedia/commons/e/e6/Jorasanko_Thakur_Bari.jpg', 'https://en.wikipedia.org/wiki/Jorasanko_Thakur_Bari'),
('16', 'Science City', 'Museum', 'Science Center', 'The largest science center in the Indian subcontinent, featuring interactive exhibits and a space theatre.', 'Inaugurated in 1997, it was developed by the National Council of Science Museums to popularize science.', NULL, 'Great for kids. The Time Machine ride and the 3D show are popular. Plan for at least 3-4 hours.', 22.5392, 88.3963, NULL, '₹70 (Entry) + Rides extra', 'Morning to afternoon', 'Science fairs and exhibitions', NULL, 'https://upload.wikimedia.org/wikipedia/commons/a/a6/Science_City_Kolkata.jpg', 'https://en.wikipedia.org/wiki/Science_City,_Kolkata'),
('17', 'Nicco Park', 'Park', 'Amusement Park', 'An amusement park often called the Disneyland of West Bengal.', 'Opened in 1991, it was the first amusement park in India to achieve ISO 9001 certification.', NULL, 'Buy the package ticket that includes all rides to save money. The water park Wet-O-Wild is great in summer.', 22.5714, 88.4217, NULL, '₹300 (Entry) to ₹1000 (Package)', 'Winter days', 'Musical nights', NULL, 'https://upload.wikimedia.org/wikipedia/commons/e/e3/Nicco_Park_Kolkata.jpg', 'https://en.wikipedia.org/wiki/Nicco_Park'),
('18', 'Alipore Zoo', 'Park', 'Zoo', 'India''s oldest zoological park, home to a wide variety of animals including the Royal Bengal Tiger.', 'Opened in 1876 by Edward VII. It was home to Adwaita, a giant tortoise that lived for over 250 years.', NULL, 'Visit in winter to see the migratory birds. It gets very crowded on holidays.', 22.5356, 88.3326, NULL, '₹50', 'Winter mornings', 'Wildlife awareness camps', NULL, 'https://upload.wikimedia.org/wikipedia/commons/f/f9/Alipore_Zoological_Gardens_Kolkata.jpg', 'https://en.wikipedia.org/wiki/Alipore_Zoological_Gardens'),
('19', 'Eden Gardens', 'Landmark', 'Stadium', 'One of the most iconic cricket stadiums in the world, known as the Mecca of Indian Cricket.', 'Established in 1864. It is the oldest and second-largest cricket stadium in India.', NULL, 'Watching a match here is an electric experience. On non-match days, you might need permission to enter the stands.', 22.5646, 88.3433, NULL, 'Varies by match', 'During a cricket match', '1987 World Cup Final, 2016 T20 World Cup Final', NULL, 'https://upload.wikimedia.org/wikipedia/commons/3/3u/Eden_Gardens_under_floodlights.jpg', 'https://en.wikipedia.org/wiki/Eden_Gardens'),
('20', 'St. Paul''s Cathedral', 'Religious', 'Church', 'A magnificent Anglican cathedral known for its Indo-Gothic architecture.', 'Completed in 1847, it was the first Episcopal Church in Asia. The original steeple fell in an earthquake in 1934 and was rebuilt.', NULL, 'It is very quiet and peaceful inside. The stained glass windows are beautiful. Midnight Mass on Christmas Eve is special.', 22.5444, 88.3463, NULL, 'Free', 'Christmas season', 'Christmas Midnight Mass', NULL, 'https://upload.wikimedia.org/wikipedia/commons/c/c3/St._Paul%27s_Cathedral_Kolkata.jpg', 'https://en.wikipedia.org/wiki/St._Paul%27s_Cathedral,_Kolkata'),
('21', 'Nakhoda Masjid', 'Religious', 'Mosque', 'The largest mosque in Kolkata, capable of accommodating 10,000 devotees.', 'Built in 1926 by Abdar Rahim Osman, a shipping magnate. Its architecture is influenced by Akbar''s tomb in Sikandra.', NULL, 'Visit during Eid to see it lit up. The area around it serves amazing Mughlai food.', 22.5754, 88.3544, NULL, 'Free', 'Eid festivals', 'Eid prayers', NULL, 'https://upload.wikimedia.org/wikipedia/commons/0/08/Nakhoda_Masjid_Kolkata.jpg', 'https://en.wikipedia.org/wiki/Nakhoda_Masjid'),
('22', 'Botanical Gardens', 'Park', 'Nature', 'A massive garden famous for the Great Banyan Tree, which is over 250 years old.', 'Founded in 1787 by Colonel Robert Kyd. It was originally known as the Company Bagh.', NULL, 'It is located in Shibpur (Howrah), across the river. The Great Banyan Tree looks like a forest by itself. Wear comfortable walking shoes.', 22.5544, 88.2904, NULL, '₹10 (Indians), ₹100 (Foreigners)', 'Winter mornings', 'Flower shows', NULL, 'https://upload.wikimedia.org/wikipedia/commons/f/f2/The_Great_Banyan_Tree.jpg', 'https://en.wikipedia.org/wiki/Acharya_Jagadish_Chandra_Bose_Indian_Botanic_Garden'),
('23', 'Mother House', 'Religious', 'Pilgrimage', 'The headquarters of the Missionaries of Charity and the tomb of Mother Teresa.', 'Established by Mother Teresa in 1950. It serves as a place of pilgrimage for people from all over the world.', NULL, 'The museum is small but very moving. It is a place of absolute silence and reflection.', 22.5533, 88.3636, NULL, 'Free', 'Morning', 'Mother Teresa''s canonization celebrations', NULL, 'https://upload.wikimedia.org/wikipedia/commons/0/0f/Mother_House_Kolkata.jpg', 'https://en.wikipedia.org/wiki/Missionaries_of_Charity'),
('24', 'Birla Planetarium', 'Museum', 'Science', 'The largest planetarium in Asia and the second largest in the world.', 'Inaugurated in 1963 by Jawaharlal Nehru. Its architecture is modeled on the Buddhist Stupa at Sanchi.', NULL, 'Check the show timings and language (English/Hindi/Bengali) before you go. The cosmic collision show is good.', 22.5453, 88.3476, NULL, '₹100', 'Afternoon', 'Astronomy workshops', NULL, 'https://upload.wikimedia.org/wikipedia/commons/3/3a/Birla_Planetarium_Kolkata.jpg', 'https://en.wikipedia.org/wiki/Birla_Planetarium,_Kolkata'),
('25', 'Millennium Park', 'Park', 'Riverside', 'A private park along the Hooghly River, offering great views of the Howrah Bridge.', 'Opened in 1999 as part of the Kolkata Riverside Beautification Project.', NULL, 'Good for a casual evening stroll. The food court offers decent snacks. The view of the lit-up Howrah Bridge is excellent.', 22.5726, 88.3471, NULL, '₹10', 'Evening', 'Musical fountain shows', NULL, 'https://upload.wikimedia.org/wikipedia/commons/6/62/Millennium_Park_Kolkata.jpg', 'https://en.wikipedia.org/wiki/Millennium_Park,_Kolkata');
